import React, { useState, useEffect } from "react";
import { useClientes } from "../hooks/useClientes";
import { useFacturas } from "../hooks/useFacturas";

const ClienteForm = ({ onClienteSeleccionado }) => {
  const [dni, setDni] = useState("");
  const [cliente, setCliente] = useState(null);
  const [clientesRegistrados, setClientesRegistrados] = useState([]);
  const { buscarClientePorDNI, loading, error } = useClientes();
  const { agregarCliente } = useFacturas();

  const buscarCliente = async () => {
    if (!dni || dni.length !== 8) {
      alert("Ingrese un DNI válido (8 dígitos)");
      return;
    }

    try {
      const clienteData = await buscarClientePorDNI(dni);
      setCliente(clienteData);
      agregarCliente(clienteData);
      
      // Si hay un callback, pasar los datos del cliente
      if (onClienteSeleccionado) {
        onClienteSeleccionado(clienteData);
      }
    } catch (err) {
      console.error("Error al consultar el DNI:", err);
      alert("No se encontró el cliente o la API no respondió");
      setCliente(null);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      buscarCliente();
    }
  };

  const limpiarFormulario = () => {
    setDni("");
    setCliente(null);
  };

  const registrarCliente = () => {
    if (!cliente) {
      alert("No hay cliente para registrar");
      return;
    }

    // Verificar si ya está registrado
    const yaRegistrado = clientesRegistrados.find(c => c.dni === cliente.dni);
    if (yaRegistrado) {
      alert("Este cliente ya está registrado");
      return;
    }

    // Agregar a la lista de registrados
    setClientesRegistrados([...clientesRegistrados, cliente]);
    alert("Cliente registrado exitosamente");
    limpiarFormulario();
  };

  return (
    <div className="form-card">
      <h2> Buscar Cliente (RENIEC)</h2>
      <div className="form-group">
        <input
          type="text"
          placeholder="Ingrese DNI (8 dígitos)"
          value={dni}
          onChange={(e) => setDni(e.target.value.replace(/\D/g, '').slice(0, 8))}
          onKeyPress={handleKeyPress}
          maxLength="8"
          disabled={loading}
        />
        <button 
          onClick={buscarCliente} 
          disabled={loading || !dni || dni.length !== 8}
          className="btn-primary"
        >
          {loading ? "Buscando..." : "Buscar"}
        </button>
        {cliente && (
          <button 
            onClick={limpiarFormulario} 
            className="btn-secondary"
          >
            Limpiar
          </button>
        )}
      </div>

      {error && (
        <div className="error-message">
           {error}
        </div>
      )}

      {cliente && (
        <div className="cliente-info">
          <h3>Cliente Encontrado</h3>
          <table className="tabla-cliente">
            <thead>
              <tr>
                <th>DNI</th>
                <th>Nombre Completo</th>
                <th>Nombres</th>
                <th>Apellido Paterno</th>
                <th>Apellido Materno</th>
                <th>Acción</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{cliente.dni}</td>
                <td>{cliente.nombreCompleto}</td>
                <td>{cliente.nombres}</td>
                <td>{cliente.apellidoPaterno}</td>
                <td>{cliente.apellidoMaterno}</td>
                <td>
                  <button onClick={registrarCliente} className="btn-registrar">
                    ✓ Registrar
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      )}

      {/* Tabla de clientes registrados */}
      {clientesRegistrados.length > 0 && (
        <div className="clientes-registrados">
          <h3>Clientes Registrados ({clientesRegistrados.length})</h3>
          <table className="tabla-clientes-registrados">
            <thead>
              <tr>
                <th>DNI</th>
                <th>Nombre Completo</th>
                <th>Apellido Paterno</th>
                <th>Apellido Materno</th>
              </tr>
            </thead>
            <tbody>
              {clientesRegistrados.map((cli) => (
                <tr key={cli.dni}>
                  <td>{cli.dni}</td>
                  <td>{cli.nombres}</td>
                  <td>{cli.apellidoPaterno}</td>
                  <td>{cli.apellidoMaterno}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ClienteForm;
