import React, { useState } from "react";
import { FacturaProvider } from "./context/FacturaContext";
import ClienteForm from "./components/ClienteForm";
import FacturaForm from "./components/FacturaForm";
import FacturaList from "./components/FacturaList";
import "./App.css";

function App() {
  const [facturaEditar, setFacturaEditar] = useState(null);
  const [vistaActual, setVistaActual] = useState("facturas"); // facturas, nueva, cliente

  const handleEditarFactura = (factura) => {
    setFacturaEditar(factura);
    setVistaActual("nueva");
  };

  const handleCancelarEdicion = () => {
    setFacturaEditar(null);
  };

  return (
    <FacturaProvider>
      <div className="app-container">
        <header className="app-header">
          <h1>ðŸ§¾ Sistema de FacturaciÃ³n</h1>
          <p className="app-subtitle">Integrado con RENIEC y SUNAT | ConversiÃ³n USD a PEN</p>
        </header>

        <nav className="app-nav">
          <button 
            className={vistaActual === "facturas" ? "active" : ""}
            onClick={() => setVistaActual("facturas")}
          >
            ðŸ“‹ Ver Facturas
          </button>
          <button 
            className={vistaActual === "nueva" ? "active" : ""}
            onClick={() => {
              setVistaActual("nueva");
              setFacturaEditar(null);
            }}
          >
            âž• Nueva Factura
          </button>
          <button 
            className={vistaActual === "cliente" ? "active" : ""}
            onClick={() => setVistaActual("cliente")}
          >
            ðŸ‘¤ Buscar Cliente
          </button>
        </nav>

        <main className="app-main">
          {vistaActual === "cliente" && (
            <ClienteForm />
          )}

          {vistaActual === "nueva" && (
            <FacturaForm 
              facturaEditar={facturaEditar}
              onCancelarEdicion={handleCancelarEdicion}
            />
          )}

          {vistaActual === "facturas" && (
            <FacturaList onEditarFactura={handleEditarFactura} />
          )}
        </main>

        <footer className="app-footer">
          <p>Â© 2025 Sistema de FacturaciÃ³n | PrÃ¡ctica 3 - JS | Desarrollado con React & Hooks</p>
        </footer>
      </div>
    </FacturaProvider>
  );
}

export default App;
