import React, { useState } from "react";
import "./FacturaList.css";

const FacturaList = ({ facturas }) => {
  const [facturaExpandida, setFacturaExpandida] = useState(null);

  if (!facturas || facturas.length === 0) {
    return (
      <div className="facturas-list">
        <h2>ðŸ“‹ Listado de Facturas</h2>
        <p className="sin-facturas">No hay facturas registradas</p>
      </div>
    );
  }

  const toggleFactura = (id) => {
    setFacturaExpandida(facturaExpandida === id ? null : id);
  };

  return (
    <div className="facturas-list">
      <h2>ðŸ“‹ Listado de Facturas ({facturas.length})</h2>
      
      <div className="facturas-container">
        {facturas.map((factura) => (
          <div key={factura.id} className="factura-card">
            {/* Encabezado de la factura */}
            <div className="factura-header" onClick={() => toggleFactura(factura.id)}>
              <div className="factura-info-principal">
                <span className="factura-numero">Factura #{factura.id}</span>
                <span className="factura-fecha">{factura.fecha}</span>
              </div>
              <div className="factura-info-secundaria">
                <span className="factura-cliente">
                  ðŸ‘¤ {factura.cliente?.nombreCompleto || 
                      `${factura.cliente?.nombres} ${factura.cliente?.apellidoPaterno}` ||
                      factura.empresa ||
                      "Cliente no especificado"}
                </span>
                <span className="factura-total">Total: S/ {factura.total}</span>
              </div>
              <button className="btn-expandir">
                {facturaExpandida === factura.id ? "â–¼ Ver menos" : "â–¶ Ver mÃ¡s"}
              </button>
            </div>

            {/* Detalles expandibles */}
            {facturaExpandida === factura.id && (
              <div className="factura-detalles">
                {/* InformaciÃ³n del cliente */}
                <div className="detalle-seccion">
                  <h4>ðŸ‘¤ Cliente</h4>
                  <p><strong>DNI:</strong> {factura.cliente?.dni || "N/A"}</p>
                  <p><strong>Nombre:</strong> {factura.cliente?.nombreCompleto || 
                      `${factura.cliente?.nombres} ${factura.cliente?.apellidoPaterno}` ||
                      factura.empresa ||
                      "No especificado"}</p>
                </div>

                {/* Productos */}
                {factura.productos && factura.productos.length > 0 && (
                  <div className="detalle-seccion">
                    <h4>ðŸ›’ Productos</h4>
                    <table className="tabla-productos-factura">
                      <thead>
                        <tr>
                          <th>Producto</th>
                          <th>Cantidad</th>
                          <th>Precio Unit.</th>
                          <th>Subtotal</th>
                        </tr>
                      </thead>
                      <tbody>
                        {factura.productos.map((producto, idx) => (
                          <tr key={idx}>
                            <td>{producto.nombre}</td>
                            <td>{producto.cantidadVendida || producto.cantidad}</td>
                            <td>S/ {producto.precio?.toFixed(2) || "0.00"}</td>
                            <td>S/ {producto.subtotalProducto?.toFixed(2) || 
                                (producto.precio * (producto.cantidadVendida || producto.cantidad)).toFixed(2)}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}

                {/* Totales */}
                <div className="detalle-seccion totales-factura">
                  <div className="fila-total-factura">
                    <span>Subtotal:</span>
                    <span>S/ {factura.subtotal || factura.monto || "0.00"}</span>
                  </div>
                  <div className="fila-total-factura">
                    <span>IGV (18%):</span>
                    <span>S/ {factura.igv || "0.00"}</span>
                  </div>
                  <div className="fila-total-factura total-final-factura">
                    <span>TOTAL:</span>
                    <span>S/ {factura.total}</span>
                  </div>
                </div>

                {/* MÃ©todo de pago */}
                {factura.metodoPago && (
                  <div className="detalle-seccion">
                    <h4>ðŸ’³ MÃ©todo de Pago</h4>
                    <p>
                      <strong>{factura.metodoPago === "efectivo" ? "ðŸ’µ Efectivo" : "ðŸ’³ Tarjeta"}</strong>
                      {factura.moneda && ` - ${factura.moneda === "PEN" ? "ðŸ‡µðŸ‡ª Soles" : "ðŸ’µ DÃ³lares"}`}
                    </p>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default FacturaList;
