import React, { useState, useEffect } from "react";
import axios from "axios";
import { useEmpresas } from "../hooks/useEmpresas"; // ‚úÖ Importar el hook
import "./ProductosForm.css"; // Usamos el mismo CSS que otros formularios

const EmpresaForm = () => {
  const [ruc, setRuc] = useState("");
  const [razonSocial, setRazonSocial] = useState("");
  const [direccion, setDireccion] = useState("");
  const [departamento, setDepartamento] = useState("");
  const [provincia, setProvincia] = useState("");
  const [distrito, setDistrito] = useState("");
  const [estado, setEstado] = useState("ACTIVO");
  const [condicion, setCondicion] = useState("HABIDO");
  const [loadingRegistro, setLoadingRegistro] = useState(false);
  const [empresasRegistradas, setEmpresasRegistradas] = useState([]);

  // ‚úÖ Usar el hook de empresas
  const { buscarEmpresaPorRUC, loading: loadingBusqueda, error: errorBusqueda } = useEmpresas();

  useEffect(() => {
    cargarEmpresas();
  }, []);

  const cargarEmpresas = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/empresas");
      setEmpresasRegistradas(response.data);
    } catch (error) {
      console.error("Error al cargar empresas:", error);
    }
  };

  const buscarEnSUNAT = async () => {
    if (!ruc || ruc.length !== 11) {
      alert("‚ùå Ingrese un RUC v√°lido de 11 d√≠gitos");
      return;
    }

    setLoading(true);
    try {
      const URL = `https://api.perudevs.com/api/v1/ruc/simple?document=${ruc}&key=cGVydWRldnMucHJvZHVjdGlvbi5maXRjb2RlcnMuNjhmMTkzNTcyZGZhMTIyNjA3ZTgzZTk5`;
      const res = await axios.get(URL, { timeout: 10000 });

      if (res.data && res.data.estado && res.data.mensaje === "Encontrado" && res.data.resultado) {
        const data = res.data.resultado;
        setRazonSocial(data.razon_social || data.nombre || "");
        setDireccion(data.direccion || "");
        setDepartamento(data.departamento || "");
        setProvincia(data.provincia || "");
        setDistrito(data.distrito || "");
        setEstado(data.estado || "ACTIVO");
        setCondicion(data.condicion || "HABIDO");
        alert("‚úÖ Datos encontrados en SUNAT. Verifique y registre.");
      } else {
        alert("‚ö†Ô∏è No se encontraron datos en SUNAT. Complete manualmente.");
      }
    } catch (error) {
      alert("‚ö†Ô∏è No se pudo consultar SUNAT. Complete los datos manualmente.");
    } finally {
      setLoading(false);
    }
  };

  const registrarEmpresa = async (e) => {
    e.preventDefault();

    if (!ruc || !razonSocial) {
      alert("‚ùå RUC y Raz√≥n Social son obligatorios");
      return;
    }

    const empresaData = {
      ruc,
      razon_social: razonSocial,
      direccion: direccion || "-",
      departamento: departamento || "-",
      provincia: provincia || "-",
      distrito: distrito || "-",
      estado: estado || "ACTIVO",
      condicion: condicion || "HABIDO"
    };

    try {
      setLoading(true);
      const response = await axios.post("http://localhost:8080/api/empresas", empresaData);
      
      alert("‚úÖ Empresa registrada exitosamente");
      limpiarFormulario();
      cargarEmpresas();
    } catch (error) {
      if (error.response?.status === 409) {
        alert("‚ùå Esta empresa ya est√° registrada");
      } else {
        alert(`‚ùå Error al registrar: ${error.response?.data?.message || error.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const limpiarFormulario = () => {
    setRuc("");
    setRazonSocial("");
    setDireccion("");
    setDepartamento("");
    setProvincia("");
    setDistrito("");
    setEstado("ACTIVO");
    setCondicion("HABIDO");
  };

  const eliminarEmpresa = async (id) => {
    if (!window.confirm("¬øEst√° seguro de eliminar esta empresa?")) {
      return;
    }

    try {
      await axios.delete(`http://localhost:8080/api/empresas/${id}`);
      alert("‚úÖ Empresa eliminada");
      cargarEmpresas();
    } catch (error) {
      alert("‚ùå Error al eliminar empresa");
    }
  };

  return (
    <div className="productos-container">
      <h2>üè¢ Registrar Empresa</h2>

      <form className="productos-form" onSubmit={registrarEmpresa}>
        <div className="form-row">
          <div className="form-group">
            <label>üî¢ RUC (11 d√≠gitos) *</label>
            <div style={{ display: "flex", gap: "10px" }}>
              <input
                type="text"
                value={ruc}
                onChange={(e) => setRuc(e.target.value)}
                placeholder="20123456789"
                maxLength="11"
                required
              />
              <button 
                type="button" 
                onClick={buscarEnSUNAT}
                disabled={loading || ruc.length !== 11}
                className="btn-buscar"
              >
                {loading ? "Buscando..." : "üîç Buscar"}
              </button>
            </div>
          </div>

          <div className="form-group">
            <label>üè¢ Raz√≥n Social *</label>
            <input
              type="text"
              value={razonSocial}
              onChange={(e) => setRazonSocial(e.target.value)}
              placeholder="EMPRESA S.A.C."
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label>üìç Direcci√≥n</label>
          <input
            type="text"
            value={direccion}
            onChange={(e) => setDireccion(e.target.value)}
            placeholder="Av. Principal 123"
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>üó∫Ô∏è Departamento</label>
            <input
              type="text"
              value={departamento}
              onChange={(e) => setDepartamento(e.target.value)}
              placeholder="LIMA"
            />
          </div>

          <div className="form-group">
            <label>üèôÔ∏è Provincia</label>
            <input
              type="text"
              value={provincia}
              onChange={(e) => setProvincia(e.target.value)}
              placeholder="LIMA"
            />
          </div>

          <div className="form-group">
            <label>üèòÔ∏è Distrito</label>
            <input
              type="text"
              value={distrito}
              onChange={(e) => setDistrito(e.target.value)}
              placeholder="SAN ISIDRO"
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>üìä Estado</label>
            <select value={estado} onChange={(e) => setEstado(e.target.value)}>
              <option value="ACTIVO">ACTIVO</option>
              <option value="BAJA">BAJA DE OFICIO</option>
              <option value="SUSPENSION">SUSPENSI√ìN TEMPORAL</option>
            </select>
          </div>

          <div className="form-group">
            <label>‚úÖ Condici√≥n</label>
            <select value={condicion} onChange={(e) => setCondicion(e.target.value)}>
              <option value="HABIDO">HABIDO</option>
              <option value="NO HABIDO">NO HABIDO</option>
            </select>
          </div>
        </div>

        <div className="form-actions">
          <button type="submit" disabled={loading}>
            {loading ? "Guardando..." : "‚úÖ Registrar Empresa"}
          </button>
          <button type="button" onClick={limpiarFormulario}>
            üîÑ Limpiar
          </button>
        </div>
      </form>

      {/* TABLA DE EMPRESAS REGISTRADAS */}
      <div className="tabla-productos">
        <h3>üìã Empresas Registradas ({empresasRegistradas.length})</h3>
        <table>
          <thead>
            <tr>
              <th>RUC</th>
              <th>Raz√≥n Social</th>
              <th>Direcci√≥n</th>
              <th>Ubicaci√≥n</th>
              <th>Estado</th>
              <th>Condici√≥n</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {empresasRegistradas.length === 0 ? (
              <tr>
                <td colSpan="7" style={{ textAlign: "center", padding: "20px" }}>
                  No hay empresas registradas
                </td>
              </tr>
            ) : (
              empresasRegistradas.map((empresa) => (
                <tr key={empresa.id_empresa}>
                  <td>{empresa.ruc}</td>
                  <td>{empresa.razon_social}</td>
                  <td>{empresa.direccion}</td>
                  <td>
                    {empresa.distrito}, {empresa.provincia}, {empresa.departamento}
                  </td>
                  <td>
                    <span className={`badge ${empresa.estado === 'ACTIVO' ? 'badge-success' : 'badge-danger'}`}>
                      {empresa.estado}
                    </span>
                  </td>
                  <td>
                    <span className={`badge ${empresa.condicion === 'HABIDO' ? 'badge-success' : 'badge-warning'}`}>
                      {empresa.condicion}
                    </span>
                  </td>
                  <td>
                    <button
                      onClick={() => eliminarEmpresa(empresa.id_empresa)}
                      className="btn-eliminar"
                    >
                      üóëÔ∏è
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EmpresaForm;
