import React, { useState } from "react";
import { useClientes } from "../hooks/useClientes";
import axios from "axios";
import "./VentasForm.css";

const VentasForm = ({ productos, clientesRegistrados, agregarFactura }) => {
  // Estados para cliente o empresa
  const [tipoCliente, setTipoCliente] = useState("persona"); // "persona" o "empresa"
  const [dni, setDni] = useState("");
  const [ruc, setRuc] = useState("");
  const [clienteSeleccionado, setClienteSeleccionado] = useState(null);
  const [empresaSeleccionada, setEmpresaSeleccionada] = useState(null);
  const { buscarClientePorDNI, loading: loadingCliente } = useClientes();
  const [loadingEmpresa, setLoadingEmpresa] = useState(false);

  // Estados para productos
  const [codigoProducto, setCodigoProducto] = useState("");
  const [cantidadProducto, setCantidadProducto] = useState(1);
  const [productosSeleccionados, setProductosSeleccionados] = useState([]);

  // Estados para factura
  const [metodoPago, setMetodoPago] = useState("efectivo");
  const [moneda, setMoneda] = useState("PEN");
  const [tipoCambio, setTipoCambio] = useState(null);

  // Buscar cliente por DNI, nombre o apellido
  const buscarCliente = async () => {
    if (!dni) {
      alert("Ingrese DNI, nombre o apellido del cliente");
      return;
    }

    const busqueda = dni.toLowerCase().trim();

    // Si tiene 8 d√≠gitos, buscar por DNI en la API
    if (busqueda.length === 8 && /^\d+$/.test(busqueda)) {
      try {
        const clienteData = await buscarClientePorDNI(busqueda);
        setClienteSeleccionado(clienteData);
        return;
      } catch (err) {
        // Si falla la API, buscar localmente
      }
    }

    // Buscar en clientes registrados localmente (por DNI, nombre o apellido)
    const clienteLocal = clientesRegistrados.find(c => 
      c.dni === busqueda ||
      c.dni.includes(busqueda) ||
      (c.nombres && c.nombres.toLowerCase().includes(busqueda)) ||
      (c.nombreCompleto && c.nombreCompleto.toLowerCase().includes(busqueda)) ||
      (c.apellidoPaterno && c.apellidoPaterno.toLowerCase().includes(busqueda)) ||
      (c.apellidoMaterno && c.apellidoMaterno.toLowerCase().includes(busqueda))
    );

    if (clienteLocal) {
      setClienteSeleccionado(clienteLocal);
    } else {
      alert("Cliente no encontrado. Aseg√∫rese de registrarlo primero en la secci√≥n 'Clientes'");
      setClienteSeleccionado(null);
    }
  };

  // Buscar y agregar producto
  const agregarProducto = () => {
    if (!codigoProducto) {
      alert("Ingrese un c√≥digo de producto");
      return;
    }

    const producto = productos.find(p => 
      String(p.id).toLowerCase().includes(codigoProducto.toLowerCase()) ||
      p.nombre.toLowerCase().includes(codigoProducto.toLowerCase())
    );

    if (!producto) {
      alert("Producto no encontrado");
      return;
    }

    // Verificar si ya existe en la lista
    const existe = productosSeleccionados.find(p => p.id === producto.id);
    if (existe) {
      alert("Este producto ya est√° agregado. Modifique la cantidad en la tabla.");
      return;
    }

    const productoConCantidad = {
      ...producto,
      cantidadVendida: parseInt(cantidadProducto),
      subtotalProducto: producto.precio * parseInt(cantidadProducto)
    };

    setProductosSeleccionados([...productosSeleccionados, productoConCantidad]);
    setCodigoProducto("");
    setCantidadProducto(1);
  };

  // Eliminar producto de la lista
  const eliminarProducto = (id) => {
    setProductosSeleccionados(productosSeleccionados.filter(p => p.id !== id));
  };

  // Actualizar cantidad
  const actualizarCantidad = (id, nuevaCantidad) => {
    if (nuevaCantidad < 1) return;
    
    setProductosSeleccionados(productosSeleccionados.map(p => 
      p.id === id 
        ? { ...p, cantidadVendida: parseInt(nuevaCantidad), subtotalProducto: p.precio * parseInt(nuevaCantidad) }
        : p
    ));
  };

  // Calcular totales
  const calcularSubtotal = () => {
    return productosSeleccionados.reduce((total, p) => total + p.subtotalProducto, 0);
  };

  const calcularIGV = () => {
    return calcularSubtotal() * 0.18;
  };

  const calcularTotal = () => {
    return calcularSubtotal() + calcularIGV();
  };

  // Generar factura
  const generarFactura = () => {
    if (!clienteSeleccionado) {
      alert("Debe seleccionar un cliente");
      return;
    }

    if (productosSeleccionados.length === 0) {
      alert("Debe agregar al menos un producto");
      return;
    }

    const nuevaFactura = {
      id: Date.now(),
      fecha: new Date().toLocaleString(),
      cliente: clienteSeleccionado,
      productos: productosSeleccionados,
      subtotal: calcularSubtotal().toFixed(2),
      igv: calcularIGV().toFixed(2),
      total: calcularTotal().toFixed(2),
      metodoPago: metodoPago,
      moneda: moneda
    };

    agregarFactura(nuevaFactura);
    alert("¬°Factura generada exitosamente!");
    
    // Limpiar formulario
    limpiarFormulario();
  };

  const limpiarFormulario = () => {
    setDni("");
    setClienteSeleccionado(null);
    setCodigoProducto("");
    setCantidadProducto(1);
    setProductosSeleccionados([]);
    setMetodoPago("efectivo");
    setMoneda("PEN");
  };

  return (
    <div className="ventas-container">
      <h2>üìù Registrar Venta</h2>

      {/* Secci√≥n 1: Buscar Cliente */}
      <div className="seccion-venta">
        <h3>üîç Buscar Cliente</h3>
        <div className="form-group-inline">
          <input
            type="text"
            placeholder="DNI, nombre o apellido del cliente"
            value={dni}
            onChange={(e) => setDni(e.target.value)}
          />
          <button onClick={buscarCliente} className="btn-buscar" disabled={loadingCliente}>
            {loadingCliente ? "Buscando..." : "üîç Buscar"}
          </button>
        </div>

        {clienteSeleccionado && (
          <div className="cliente-seleccionado">
            <p><strong>Cliente:</strong> {clienteSeleccionado.nombreCompleto || `${clienteSeleccionado.nombres} ${clienteSeleccionado.apellidoPaterno}`}</p>
            <p><strong>DNI:</strong> {clienteSeleccionado.dni}</p>
          </div>
        )}
      </div>

      {/* Secci√≥n 2: Buscar y Agregar Producto */}
      <div className="seccion-venta">
        <h3>üîç Buscar Producto</h3>
        <div className="form-group-inline">
          <input
            type="text"
            placeholder="C√≥digo o nombre del producto"
            value={codigoProducto}
            onChange={(e) => setCodigoProducto(e.target.value)}
          />
          <input
            type="number"
            placeholder="Cantidad"
            value={cantidadProducto}
            onChange={(e) => setCantidadProducto(e.target.value)}
            min="1"
            className="input-cantidad"
          />
          <button onClick={agregarProducto} className="btn-agregar">
            ‚ûï Agregar
          </button>
        </div>
      </div>

      {/* Secci√≥n 3: Tabla de Productos */}
      {productosSeleccionados.length > 0 && (
        <div className="seccion-venta">
          <h3>üõí Productos Seleccionados</h3>
          <table className="tabla-productos-venta">
            <thead>
              <tr>
                <th>C√≥digo</th>
                <th>Producto</th>
                <th>Precio Unit.</th>
                <th>Cantidad</th>
                <th>Subtotal</th>
                <th>Acci√≥n</th>
              </tr>
            </thead>
            <tbody>
              {productosSeleccionados.map((producto) => (
                <tr key={producto.id}>
                  <td>{producto.id}</td>
                  <td>{producto.nombre}</td>
                  <td>S/ {producto.precio.toFixed(2)}</td>
                  <td>
                    <input
                      type="number"
                      value={producto.cantidadVendida}
                      onChange={(e) => actualizarCantidad(producto.id, e.target.value)}
                      min="1"
                      className="input-cantidad-tabla"
                    />
                  </td>
                  <td>S/ {producto.subtotalProducto.toFixed(2)}</td>
                  <td>
                    <button 
                      onClick={() => eliminarProducto(producto.id)} 
                      className="btn-eliminar"
                    >
                      üóëÔ∏è
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Secci√≥n 4: Totales y M√©todo de Pago */}
      {productosSeleccionados.length > 0 && (
        <div className="seccion-venta">
          <div className="totales-container">
            <div className="totales">
              <div className="fila-total">
                <span>Subtotal:</span>
                <span>S/ {calcularSubtotal().toFixed(2)}</span>
              </div>
              <div className="fila-total">
                <span>IGV (18%):</span>
                <span>S/ {calcularIGV().toFixed(2)}</span>
              </div>
              <div className="fila-total total-final">
                <span>TOTAL:</span>
                <span>S/ {calcularTotal().toFixed(2)}</span>
              </div>
            </div>

            <div className="metodo-pago">
              <h4>üí≥ M√©todo de Pago</h4>
              <div className="opciones-pago">
                <label>
                  <input
                    type="radio"
                    value="efectivo"
                    checked={metodoPago === "efectivo"}
                    onChange={(e) => setMetodoPago(e.target.value)}
                  />
                  üíµ Efectivo
                </label>
                <label>
                  <input
                    type="radio"
                    value="tarjeta"
                    checked={metodoPago === "tarjeta"}
                    onChange={(e) => setMetodoPago(e.target.value)}
                  />
                  üí≥ Tarjeta
                </label>
              </div>

              <div className="opciones-moneda">
                <label>
                  <input
                    type="radio"
                    value="PEN"
                    checked={moneda === "PEN"}
                    onChange={(e) => setMoneda(e.target.value)}
                  />
                  üáµüá™ Soles
                </label>
                <label>
                  <input
                    type="radio"
                    value="USD"
                    checked={moneda === "USD"}
                    onChange={(e) => setMoneda(e.target.value)}
                  />
                  üíµ D√≥lares
                </label>
              </div>
            </div>
          </div>

          <button onClick={generarFactura} className="btn-generar-factura">
            ‚úÖ Generar Factura
          </button>
        </div>
      )}
    </div>
  );
};

export default VentasForm;
