import React, { useState, useEffect } from "react";
import { useClientes } from "../hooks/useClientes";
import { useFacturas } from "../hooks/useFacturas";
import { useTipoCambio } from "../hooks/useTipoCambio";

const FacturaForm = ({ facturaEditar, onCancelarEdicion }) => {
  const [ruc, setRuc] = useState("");
  const [empresa, setEmpresa] = useState(null);
  const [clienteDNI, setClienteDNI] = useState("");
  const [clienteNombre, setClienteNombre] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [monto, setMonto] = useState("");
  const [moneda, setMoneda] = useState("PEN");
  const [metodoPago, setMetodoPago] = useState("efectivo");
  
  const { buscarEmpresaPorRUC, buscarClientePorDNI, loading } = useClientes();
  const { agregarFactura, actualizarFactura } = useFacturas();
  const { tipoCambio, convertirUSDtoSoles, obtenerTipoCambio } = useTipoCambio();

  // Cargar datos si estamos editando
  useEffect(() => {
    if (facturaEditar) {
      setRuc(facturaEditar.ruc || "");
      setEmpresa(facturaEditar.empresa ? { razonSocial: facturaEditar.empresa } : null);
      setClienteDNI(facturaEditar.clienteDNI || "");
      setClienteNombre(facturaEditar.clienteNombre || "");
      setDescripcion(facturaEditar.descripcion || "");
      setMonto(facturaEditar.montoOriginal || "");
      setMoneda(facturaEditar.monedaOriginal || "PEN");
      setMetodoPago(facturaEditar.metodoPago || "efectivo");
    }
  }, [facturaEditar]);

  const buscarEmpresa = async () => {
    if (!ruc || ruc.length !== 11) {
      alert("Ingrese un RUC vÃ¡lido (11 dÃ­gitos)");
      return;
    }

    try {
      const empresaData = await buscarEmpresaPorRUC(ruc);
      setEmpresa(empresaData);
    } catch (err) {
      alert("Empresa no encontrada");
      setEmpresa(null);
    }
  };

  const buscarCliente = async () => {
    if (!clienteDNI || clienteDNI.length !== 8) {
      alert("Ingrese un DNI vÃ¡lido (8 dÃ­gitos)");
      return;
    }

    try {
      const clienteData = await buscarClientePorDNI(clienteDNI);
      setClienteNombre(clienteData.nombreCompleto);
    } catch (err) {
      alert("Cliente no encontrado");
    }
  };

  const calcularFactura = async () => {
    if (!empresa || !monto || !clienteDNI || !descripcion) {
      alert("Por favor complete todos los campos");
      return;
    }

    // Actualizar tipo de cambio
    await obtenerTipoCambio();

    let montoEnSoles = parseFloat(monto);
    const monedaOriginal = moneda;
    
    // Convertir si es en dÃ³lares
    if (moneda === "USD") {
      montoEnSoles = parseFloat(convertirUSDtoSoles(monto));
    }

    const igv = montoEnSoles * 0.18;
    const total = montoEnSoles + igv;

    const nuevaFactura = {
      id: facturaEditar?.id || Date.now(),
      fecha: facturaEditar?.fecha || new Date().toLocaleString('es-PE'),
      ruc: ruc,
      empresa: empresa.razonSocial,
      direccionEmpresa: empresa.direccion || "No especificada",
      estadoEmpresa: empresa.estado || "Activo",
      clienteDNI: clienteDNI,
      clienteNombre: clienteNombre,
      descripcion: descripcion,
      montoOriginal: monto,
      monedaOriginal: monedaOriginal,
      monto: montoEnSoles.toFixed(2),
      igv: igv.toFixed(2),
      total: total.toFixed(2),
      moneda: "PEN",
      tipoCambioUsado: moneda === "USD" ? tipoCambio.toFixed(2) : null,
      metodoPago: metodoPago,
      estado: "pagado"
    };

    if (facturaEditar) {
      actualizarFactura(nuevaFactura);
      alert("Factura actualizada exitosamente");
      onCancelarEdicion();
    } else {
      agregarFactura(nuevaFactura);
      alert("Factura generada exitosamente");
    }

    limpiarFormulario();
  };

  const limpiarFormulario = () => {
    setRuc("");
    setEmpresa(null);
    setClienteDNI("");
    setClienteNombre("");
    setDescripcion("");
    setMonto("");
    setMoneda("PEN");
    setMetodoPago("efectivo");
  };

  const cancelarEdicion = () => {
    limpiarFormulario();
    if (onCancelarEdicion) {
      onCancelarEdicion();
    }
  };

  return (
    <div className="form-card">
      <h2> {facturaEditar ? "Editar" : "Registrar"} Factura</h2>

      <div className="form-section">
        <h3>Datos de la Empresa (SUNAT)</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="RUC Empresa (11 dÃ­gitos)"
            value={ruc}
            onChange={(e) => setRuc(e.target.value.replace(/\D/g, '').slice(0, 11))}
            maxLength="11"
            disabled={loading}
          />
          <button 
            onClick={buscarEmpresa} 
            disabled={loading || !ruc || ruc.length !== 11}
            className="btn-primary"
          >
            {loading ? "Buscando..." : "Buscar Empresa"}
          </button>
        </div>

        {empresa && (
          <div className="empresa-info">
            <p><b>RazÃ³n Social:</b> {empresa.razonSocial}</p>
            <p><b>DirecciÃ³n:</b> {empresa.direccion}</p>
            <p><b>Estado:</b> {empresa.estado}</p>
          </div>
        )}
      </div>

      <div className="form-section">
        <h3>Datos del Cliente</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="DNI Cliente (8 dÃ­gitos)"
            value={clienteDNI}
            onChange={(e) => setClienteDNI(e.target.value.replace(/\D/g, '').slice(0, 8))}
            maxLength="8"
          />
          <button 
            onClick={buscarCliente} 
            disabled={loading || !clienteDNI || clienteDNI.length !== 8}
            className="btn-primary"
          >
            Buscar Cliente
          </button>
        </div>
        {clienteNombre && (
          <p className="cliente-nombre"><b>Cliente:</b> {clienteNombre}</p>
        )}
      </div>

      <div className="form-section">
        <h3>Detalles de la Factura</h3>
        <textarea
          placeholder="DescripciÃ³n de productos/servicios"
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
          rows="3"
        />

        <div className="form-row">
          <input
            type="number"
            placeholder="Monto"
            value={monto}
            onChange={(e) => setMonto(e.target.value)}
            step="0.01"
            min="0"
          />
          <select value={moneda} onChange={(e) => setMoneda(e.target.value)}>
            <option value="PEN">Soles (PEN)</option>
            <option value="USD">DÃ³lares (USD)</option>
          </select>
        </div>

        {moneda === "USD" && (
          <div className="tipo-cambio-info">
            ðŸ’± Tipo de cambio: S/ {tipoCambio.toFixed(2)} por USD
            {monto && (
              <span> â†’ Total en soles: S/ {convertirUSDtoSoles(monto)}</span>
            )}
          </div>
        )}

        <select 
          value={metodoPago} 
          onChange={(e) => setMetodoPago(e.target.value)}
          className="metodo-pago"
        >
          <option value="efectivo">Efectivo</option>
          <option value="tarjeta"> Tarjeta</option>
          <option value="transferencia"> Transferencia</option>
          <option value="yape"> Yape/Plin</option>
        </select>
      </div>

      <div className="form-actions">
        <button 
          onClick={calcularFactura} 
          className="btn-success"
          disabled={loading}
        >
          {facturaEditar ? "ðŸ’¾Actualizar Factura" : "Generar Factura"}
        </button>
        
        {facturaEditar && (
          <button 
            onClick={cancelarEdicion} 
            className="btn-secondary"
          >
             Cancelar
          </button>
        )}
        
        {!facturaEditar && (
          <button 
            onClick={limpiarFormulario} 
            className="btn-secondary"
          >
             Limpiar
