import React, { useState } from "react";
import ClienteForm from "./components/ClienteForm";
import FacturaForm from "./components/FacturaForm";
import FacturaList from "./components/FacturaList";
import Login from "./components/LoginForm";
import ProductosForm from "./components/ProductosForm";
import "./App.css";
import { FacturaProvider } from "./context/FacturaContext";


function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);  // ← Agregar este estado
    const [facturas, setFacturas] = useState([]);
  
    const agregarFactura = (factura) => {
      setFacturas([...facturas, factura]);
    };
  
    const handleLogin = () => {
      setIsLoggedIn(true);  // ← Cambiar a true cuando login exitoso
    };
  
    return (
      <div className="app-container">
        {!isLoggedIn ? (  // ← Si NO está logueado
          <Login onLogin={handleLogin} />  // ← Mostrar SOLO Login
        ) : (  // ← Si SÍ está logueado
          <>
            <h1>Sistema de Facturación</h1>
            <button onClick={() => setIsLoggedIn(false)}>Cerrar Sesión</button>
            <FacturaProvider>
              <ClienteForm />
              <ProductosForm />
              <ListaProductos />
              <FacturaForm agregarFactura={agregarFactura} />
              <FacturaList facturas={facturas} />
            </FacturaProvider>
           
          </>
        )}
      </div>
    );
  }

export default App;
