import React, { useState } from "react";
import Sildebar from "./components/SildebarForm";  // Tu archivo
import ClienteForm from "./components/ClienteForm";
import FacturaForm from "./components/FacturaForm";
import FacturaList from "./components/FacturaList";
import ProductosForm from "./components/ProductosForm";
import ListaProductos from "./components/ListaProductos";
import Login from "./components/LoginForm";
import "./App.css";
import "./components/Sildebar.css";
import { FacturaProvider } from "./context/FacturaContext";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [seccionActiva, setSeccionActiva] = useState("inicio");
  const [facturas, setFacturas] = useState([]);
  const [productos, setProductos] = useState([]);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setSeccionActiva("inicio");
  };

  const agregarFactura = (factura) => {
    setFacturas([...facturas, factura]);
  };

  const agregarProducto = (producto) => {
    setProductos([...productos, producto]);
  };

  // Renderiza el contenido según la sección
  const renderContenido = () => {
    switch (seccionActiva) {
      case "inicio":
        return (
          <div className="seccion-inicio">
            <h2>Bienvenido al Sistema de Facturación</h2>
            <div className="estadisticas">
              <div className="card">
                <h3>Total Facturas</h3>
                <p className="numero">{facturas.length}</p>
              </div>
              <div className="card">
                <h3>Total Productos</h3>
                <p className="numero">{productos.length}</p>
              </div>
            </div>
          </div>
        );

      case "clientes":
        return (
          <FacturaProvider>
            <ClienteForm />
          </FacturaProvider>
        );

      case "productos":
        return (
          <div>
            <ProductosForm agregarProducto={agregarProducto} />
            <ListaProductos productos={productos} />
          </div>
        );

      case "facturas":
        return (
          <FacturaProvider>
            <FacturaList facturas={facturas} />
          </FacturaProvider>
        );

      case "nueva-factura":
        return (
          <FacturaProvider>
            <FacturaForm agregarFactura={agregarFactura} />
          </FacturaProvider>
        );

      case "reportes":
        return (
          <div>
            <h2>Reportes</h2>
            <p>Aquí irán los reportes y estadísticas</p>
          </div>
        );

      default:
        return <div><h2>Selecciona una opción del menú</h2></div>;
    }
  };

  return (
    <div className="app-container">
      {!isLoggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <Sidebar 
            seccionActiva={seccionActiva} 
            cambiarSeccion={setSeccionActiva}
            cerrarSesion={handleLogout}
          />
          <main className="contenido-principal">
            {renderContenido()}
          </main>
        </>
      )}
    </div>
  );
}

export default App;
