import React, { useState, useEffect } from "react";
import { useClientes } from "../hooks/useClientes";
import { useFacturas } from "../hooks/useFacturas";
import "./ClienteForm.css";

const ClienteForm = ({ onClienteSeleccionado, clientesRegistrados = [], agregarClienteRegistrado }) => {
  const [dni, setDni] = useState("");
  const [cliente, setCliente] = useState(null);
  const [clientesBackend, setClientesBackend] = useState([]);

  const {
    buscarClientePorDNI,
    guardarClienteEnBackend,
    obtenerClientesRegistrados,
    buscarClienteEnBackend,
    loading,
    error
  } = useClientes();

  const { agregarCliente } = useFacturas();

  // Cargar clientes del backend al montar el componente
  useEffect(() => {
    cargarClientesBackend();
  }, []);

  const cargarClientesBackend = async () => {
    try {
      const clientes = await obtenerClientesRegistrados();
      setClientesBackend(clientes);
    } catch (err) {
      console.error("Error al cargar clientes:", err);
    }
  };

  const buscarCliente = async () => {
    if (!dni || dni.length !== 8) {
      alert("Ingrese un DNI v√°lido (8 d√≠gitos)");
      return;
    }

    try {
      const clienteEnBackend = await buscarClienteEnBackend(dni);

      if (clienteEnBackend && clienteEnBackend.length > 0) {  // Verificar array no vac√≠o
        alert("Este cliente ya est√° registrado en la base de datos");
        setCliente({
          dni: clienteEnBackend[0].dni,  // Acceder al primer elemento
          nombres: clienteEnBackend[0].nombre_completo?.split(' ')[0] || '',
          apellidoPaterno: clienteEnBackend[0].apellido_paterno,
          apellidoMaterno: clienteEnBackend[0].apellido_materno,
          nombreCompleto: clienteEnBackend[0].nombre_completo,
          yaRegistrado: true
        });
        return;
      }

      // Si no existe, buscar en RENIEC
      const clienteData = await buscarClientePorDNI(dni);
      setCliente({ ...clienteData, yaRegistrado: false });
      agregarCliente(clienteData);

      // Si hay un callback, pasar los datos del cliente
      if (onClienteSeleccionado) {
        onClienteSeleccionado(clienteData);
      }
    } catch (err) {
      console.error("Error al consultar el DNI:", err);
      alert("No se encontr√≥ el cliente o la API no respondi√≥");
      setCliente(null);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      buscarCliente();
    }
  };

  const limpiarFormulario = () => {
    setDni("");
    setCliente(null);
  };

  const registrarCliente = async () => {
    if (!cliente) {
      alert("No hay cliente para registrar");
      return;
    }

    if (cliente.yaRegistrado) {
      alert("Este cliente ya est√° registrado");
      return;
    }

    try {
      // Guardar en el backend (Spring Boot)
      const clienteGuardado = await guardarClienteEnBackend(cliente);

      alert("Cliente registrado exitosamente en la base de datos");

      // Recargar la lista de clientes
      await cargarClientesBackend();

      // Usar la funci√≥n del padre si existe
      if (agregarClienteRegistrado) {
        agregarClienteRegistrado(cliente);
      }

      limpiarFormulario();
    } catch (err) {
      console.error("Error al registrar cliente:", err);
      alert("Error al registrar el cliente en la base de datos");
    }
  };

  return (
    <div className="cliente-form-container">
      <h2 className="cliente-form-titulo">üîç Buscar Cliente (RENIEC)</h2>
      
      <div className="busqueda-cliente">
        <h3>üîé Buscar por DNI</h3>
        <div className="search-box">
          <input
            type="text"
            className="search-input"
            placeholder="Ingrese DNI (8 d√≠gitos)"
            value={dni}
            onChange={(e) => setDni(e.target.value.replace(/\D/g, '').slice(0, 8))}
            onKeyPress={handleKeyPress}
            maxLength="8"
            disabled={loading}
          />
          <button
            onClick={buscarCliente}
            disabled={loading || !dni || dni.length !== 8}
            className="btn-buscar"
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                Buscando...
              </>
            ) : (
              <>üîç Buscar</>
            )}
          </button>
          {cliente && (
            <button
              onClick={limpiarFormulario}
              className="btn-buscar"
              style={{ background: '#e74c3c' }}
            >
              üóëÔ∏è Limpiar
            </button>
          )}
        </div>
      </div>

      {error && (
        <div className="error-message">
          ‚ö†Ô∏è {error}
        </div>
      )}

      {cliente && (
        <div className="cliente-encontrado">
          <h4>‚úÖ Cliente Encontrado {cliente.yaRegistrado && '(Ya Registrado)'}</h4>
          <div className="cliente-info-grid">
            <div className="cliente-info-item">
              <span className="cliente-info-label">DNI</span>
              <span className="cliente-info-value">
                <span className="badge-dni">{cliente.dni}</span>
              </span>
            </div>
            <div className="cliente-info-item">
              <span className="cliente-info-label">Nombre Completo</span>
              <span className="cliente-info-value">{cliente.nombreCompleto}</span>
            </div>
            <div className="cliente-info-item">
              <span className="cliente-info-label">Nombres</span>
              <span className="cliente-info-value">{cliente.nombres}</span>
            </div>
            <div className="cliente-info-item">
              <span className="cliente-info-label">Apellido Paterno</span>
              <span className="cliente-info-value">{cliente.apellidoPaterno}</span>
            </div>
            <div className="cliente-info-item">
              <span className="cliente-info-label">Apellido Materno</span>
              <span className="cliente-info-value">{cliente.apellidoMaterno || '-'}</span>
            </div>
          </div>
          
          {!cliente.yaRegistrado && (
            <button onClick={registrarCliente} className="btn-registrar-cliente">
              ‚úì Registrar en Base de Datos
            </button>
          )}
        </div>
      )}

      {/* Tabla de clientes registrados en el BACKEND */}
      {clientesBackend.length > 0 ? (
        <div className="clientes-registrados">
          <h3>üìã Clientes en Base de Datos ({clientesBackend.length})</h3>
          <div className="tabla-clientes-wrapper">
            <table className="tabla-clientes">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>DNI</th>
                  <th>Nombre Completo</th>
                  <th>Apellido Paterno</th>
                  <th>Apellido Materno</th>
                </tr>
              </thead>
              <tbody>
                {clientesBackend.map((cli) => (
                  <tr key={cli.id_cliente}>
                    <td>{cli.id_cliente}</td>
                    <td><span className="badge-dni">{cli.dni}</span></td>
                    <td><strong>{cli.nombre_completo}</strong></td>
                    <td>{cli.apellido_paterno}</td>
                    <td>{cli.apellido_materno || '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        clientesBackend.length === 0 && !loading && (
          <div className="sin-clientes">
            <div className="sin-clientes-icon">üë•</div>
            <p>No hay clientes registrados</p>
            <small>Busca un cliente por DNI y reg√≠stralo en la base de datos</small>
          </div>
        )
      )}
    </div>
  );
};

export default ClienteForm;
