import React, { useState } from "react";
import { useFacturas } from "../hooks/useFacturas";

const FacturaList = ({ onEditarFactura }) => {
  const { state, eliminarFactura } = useFacturas();
  const { facturas } = state;
  const [filtro, setFiltro] = useState("");
  const [ordenar, setOrdenar] = useState("fecha-desc");

  const confirmarEliminar = (id, empresa) => {
    if (window.confirm(`쮼st치 seguro de eliminar la factura de ${empresa}?`)) {
      eliminarFactura(id);
    }
  };

  const facturasFiltradas = facturas
    .filter(f => {
      const searchTerm = filtro.toLowerCase();
      return (
        f.empresa.toLowerCase().includes(searchTerm) ||
        f.clienteNombre.toLowerCase().includes(searchTerm) ||
        f.ruc.includes(searchTerm) ||
        f.clienteDNI.includes(searchTerm)
      );
    })
    .sort((a, b) => {
      switch (ordenar) {
        case "fecha-desc":
          return b.id - a.id;
        case "fecha-asc":
          return a.id - b.id;
        case "monto-desc":
          return parseFloat(b.total) - parseFloat(a.total);
        case "monto-asc":
          return parseFloat(a.total) - parseFloat(b.total);
        case "empresa":
          return a.empresa.localeCompare(b.empresa);
        default:
          return 0;
      }
    });

  const totalGeneral = facturas.reduce((sum, f) => sum + parseFloat(f.total), 0);
  const totalFiltrado = facturasFiltradas.reduce((sum, f) => sum + parseFloat(f.total), 0);

  return (
    <div className="facturas-container">
      <div className="facturas-header">
        <h2>Listado de Facturas ({facturas.length})</h2>
        
        <div className="facturas-stats">
          <div className="stat-card">
            <span className="stat-label">Total General:</span>
            <span className="stat-value">S/ {totalGeneral.toFixed(2)}</span>
          </div>
          {filtro && (
            <div className="stat-card">
              <span className="stat-label">Total Filtrado:</span>
              <span className="stat-value">S/ {totalFiltrado.toFixed(2)}</span>
            </div>
          )}
        </div>
      </div>

      <div className="facturas-controls">
        <input
          type="text"
          placeholder="游댌 Buscar por empresa, cliente, RUC o DNI..."
          value={filtro}
          onChange={(e) => setFiltro(e.target.value)}
          className="search-input"
        />
        
        <select 
          value={ordenar} 
          onChange={(e) => setOrdenar(e.target.value)}
          className="sort-select"
        >
          <option value="fecha-desc">M치s recientes</option>
          <option value="fecha-asc"> M치s antiguas</option>
          <option value="monto-desc"> Mayor monto</option>
          <option value="monto-asc">Menor monto</option>
          <option value="empresa">Por empresa (A-Z)</option>
        </select>
      </div>

      {facturasFiltradas.length === 0 ? (
        <div className="no-facturas">
          {filtro ? (
            <p>No se encontraron facturas que coincidan con "{filtro}"</p>
          ) : (
            <p>No hay facturas registradas. 춰Crea tu primera factura!</p>
          )}
        </div>
      ) : (
        <div className="facturas-list">
          {facturasFiltradas.map((factura) => (
            <div key={factura.id} className="factura-card">
              <div className="factura-header-card">
                <div className="factura-numero">
                  <span className="factura-label">Factura #</span>
                  <span className="factura-id">{factura.id}</span>
                </div>
                <div className="factura-fecha">
                   {factura.fecha}
                </div>
              </div>

              <div className="factura-body">
                <div className="factura-section">
                  <h4> Empresa Emisora</h4>
                  <p><b>{factura.empresa}</b></p>
                  <p className="text-small">RUC: {factura.ruc}</p>
                  <p className="text-small">{factura.direccionEmpresa}</p>
                </div>

                <div className="factura-section">
                  <h4> Cliente</h4>
                  <p><b>{factura.clienteNombre}</b></p>
                  <p className="text-small">DNI: {factura.clienteDNI}</p>
                </div>

                <div className="factura-section">
                  <h4>Descripci칩n</h4>
                  <p>{factura.descripcion}</p>
                </div>

                <div className="factura-montos">
                  <div className="monto-row">
                    <span>Subtotal:</span>
                    <span>S/ {factura.monto}</span>
                  </div>
                  <div className="monto-row">
                    <span>IGV (18%):</span>
                    <span>S/ {factura.igv}</span>
                  </div>
                  <div className="monto-row total">
                    <span><b>Total:</b></span>
                    <span><b>S/ {factura.total}</b></span>
                  </div>
                  
                  {factura.tipoCambioUsado && (
                    <div className="conversion-info">
                      <p className="text-small">
                         Pagado en USD: ${factura.montoOriginal}
                        <br />
                        Tipo de cambio: S/ {factura.tipoCambioUsado}
                      </p>
                    </div>
                  )}

                  <div className="metodo-pago-badge">
                    {factura.metodoPago === 'efectivo' }
                    {factura.metodoPago === 'tarjeta' }
                    {factura.metodoPago === 'transferencia'}
                    {factura.metodoPago === 'yape'}
                    {' '}{factura.metodoPago.charAt(0).toUpperCase() + factura.metodoPago.slice(1)}
                  </div>
                </div>
              </div>

              <div className="factura-actions">
                <button 
                  onClick={() => onEditarFactura(factura)}
                  className="btn-edit"
                >
                   Editar
                </button>
                <button 
                  onClick={() => confirmarEliminar(factura.id, factura.empresa)}
                  className="btn-delete"
                >
                  Eliminar
                </button>
                <button 
                  onClick={() => window.print()}
                  className="btn-print"
                >
                   Imprimir
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FacturaList;
