import React, { useState } from "react";
import axios from "axios";

const ClienteForm = () => {
  const [dni, setDni] = useState("");
  const [cliente, setCliente] = useState(null);

  const buscarCliente = async () => {
    if (!dni || dni.length !== 8) {
      alert("Ingrese un DNI v谩lido (8 d铆gitos)");
      return;
    }

    const key = "cGVydWRldnMucHJvZHVjdGlvbi5maXRjb2RlcnMuNjhmMTkzNTcyZGZhMTIyNjA3ZTgzZTk5";
    const url = `https://api.perudevs.com/api/v1/dni/simple?document=${dni}&key=${key}`;

    try {
      const res = await axios.get(url);
      //  algunos endpoints devuelven los datos en res.data.resultado
      const data = res.data.resultado || res.data.data || res.data;

      setCliente(data);
    } catch (err) {
      console.error("Error al consultar el DNI:", err);
      alert("No se encontr贸 el cliente o la API no respondi贸");
      setCliente(null);
    }
  };

  return (
    <div className="form-card">
      <h2>Buscar Cliente (RENIEC)</h2>
      <input
        type="text"
        placeholder="Ingrese DNI"
        value={dni}
        onChange={(e) => setDni(e.target.value)}
      />
      <button onClick={buscarCliente}>Buscar</button>

      {cliente && (
        <div className="cliente-info">
          <p>
            <b>DNI:</b> {cliente.numero || dni}
          </p>
          <p>
            <b>Nombre:</b> {cliente.nombres}
          </p>
          <p>
            <b>Apellidos:</b>{" "}
            {cliente.apellido_paterno || cliente.apellidoPaterno}{" "}
            {cliente.apellido_materno || cliente.apellidoMaterno}
          </p>
        </div>
      )}
    </div>
  );
};

export default ClienteForm;
