import React from "react";
import "./Sidebar.css";

const Sidebar = ({ seccionActiva, cambiarSeccion, cerrarSesion }) => {
  const menuItems = [
    { id: "inicio", nombre: "Inicio", icono: "üè†" },           // ‚Üê NUEVO
    { id: "clientes", nombre: "Clientes", icono: "üë•" },
    { id: "usuarios", nombre: "Usuarios", icono: "üë§" },       // ‚Üê NUEVO
    { id: "productos", nombre: "Productos", icono: "üì¶" },
    { id: "ventas", nombre: "Ventas", icono: "üõí" },
    { id: "facturas", nombre: "Facturas", icono: "üìÑ" },       // ‚Üê NUEVO (opcional)
    { id: "reportes", nombre: "Reportes", icono: "üìä" },       // ‚Üê NUEVO (opcional)
  ];

  return (
    <aside className="sidebar">
      <div className="sidebar-header">
        <h2>üíº Sistema Facturaci√≥n</h2>
      </div>
      
      <nav className="sidebar-nav">
        <ul className="menu-list">
          {menuItems.map((item) => (
            <li
              key={item.id}
              className={`menu-item ${seccionActiva === item.id ? "activo" : ""}`}
              onClick={() => cambiarSeccion(item.id)}
            >
              <span className="icono">{item.icono}</span>
              <span className="texto">{item.nombre}</span>
            </li>
          ))}
        </ul>
      </nav>

      <div className="sidebar-footer">
        <button className="btn-cerrar-sesion" onClick={cerrarSesion}>
          üö™ Cerrar Sesi√≥n
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
